<?phpnamespace lv\request {	use lv\event\Event;		class CurlEvent extends Event 	{		private $_mod;		private static $_data = '';				public static function data($str = '', $reset = FALSE)		{			if ($reset) 			{				self::$_data = $str;			}			else 			{				self::$_data .= $str;			}						return self::$_data;		}				public function setMod($str) 		{			$this->_mod = $str;			return $this;		}				public function getMod() 		{			return $this->_mod;		}	}}